mapper.update:
update category
window_id = #{windowId}, name = #{name}, type = #{type}, sort = #{sort}, create_user = #{createUser}, update_user = #{updateUser}, create_time = #{createTime}, update_time = #{updateTime}
where id = #{id}

mapper.select:
select * from category


        and window_id = #{windowId}


        and name like concat('%', #{name}, '%')


        and type = #{type}


order by sort asc

select * from category where status = 1

    and type = #{type}


    and window_id = #{windowId}

order by sort asc, create_time desc
