mapper.delete:
delete from cart


        and dish_id = #{dishId}


        and dish_flavor = #{dishFlavor}

mapper.select:
select * from cart


        and user_id = #{userId}


        and window_id = #{windowId}


        and dish_id = #{dishId}


        and setmeal_id = #{setmealId}


        and dish_flavor = #{dishFlavor}


order by create_time desc
